// @flow
import * as React from 'react';
import cx from 'classnames';

import styles from './icon_button.module.css';

type IconClassNamesType = 'arrowBack' | 'something';

// can be used to safely access different awesomeIcons;
// alternatively; components can be pre-assigned to consts with these.
export const iconClassNames: { [key: IconClassNamesType]: string } = {
  arrowBack: "fas fa-2x fa-arrow-left",
  something: 'henlo'
};

type Props = {|
    iconClassNames: string,
    onClick: () => void
|}

const IconButton = ({ iconClassNames, onClick }: Props) => (
 <i className={cx(iconClassNames, styles.base)} onClick={onClick}></i>
);

export default IconButton;
