import React, { Component } from 'react';

class Landing extends Component {
    constructor(props){
        super(props)

        this.state = {
            body_class: document.body.className,
        }
    }

    componentWillMount(){
        this.setState({
            body_class: document.body.className
        })
        document.body.className = this.state.body_class + ( this.state.body_class ? ' ' : '') + 'text-center height_100 landing';
    }

    componentWillUnmount(){
        document.body.className = this.state.body_class;
    }

    /*
        text-center height_100 landing
    */

    render() {
        return (
            <div class="d-flex w-100 h-100 p-3 mx-auto flex-column wrapper_main">
            <header class="masthead mb-auto">
                <div class="inner">
                <h3 class="float-left">Nobe</h3>
                <nav class="nav justify-content-center float-right">
                    <a class="nav-link active login-landing" href="/login">Login</a>
                </nav>
                </div>
            </header>

            <main role="main" class="inner" id="inner_wrapper">
                <h1>Start searching.</h1>
                <p class="lead">Nobe te permite buscar texto en imágenes manuscritas.</p>
                <div class="col-xs-12 display_block">
                    <input type="text" id="searchBar" class="form-control main-search-bar col-xs-10 display_inline_block" placeholder="Start searching here..." required="" autofocus=""/>
                    <span class="input-group-btn col-xs-2 main-span-button">
                        <button class="btn btn-search main-search-button" type="button"><i class="fa fa-search fa-fw display_inline_block"></i></button>
                    </span>
                </div>

            </main>

            <footer class="mastfoot mt-auto">
                <div class="inner">
                <p>Trabajo de Fin de Grado por <a href="https://github.com/ForFer">Fernando</a>, para la © Universidad Carlos III de Madrid,l 2019</p>
                </div>
            </footer>
            </div>

        )
    }
}

export default Landing;

