import React, { Component, useCallback } from 'react'
import Dropzone from 'react-dropzone'

const currentYear = new Date().getYear() + 1900;

import {useDropzone} from 'react-dropzone'

function MyDropzone(onDropCallBack) {
  const onDrop = useCallback(acceptedFiles => {
    const reader = new FileReader()

    reader.onabort = () => console.log('file reading was aborted')
    reader.onerror = () => console.log('file reading has failed')
    reader.onload = () => {
      // Do whatever you want with the file contents
      const binaryStr = reader.result
      console.log(binaryStr)
    }

    // acceptedFiles.forEach(file => reader.readAsBinaryString(file))
    acceptedFiles.forEach(element => {
        onDropCallBack(element);
    });
  }, [])
  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  return (
    <div {...getRootProps()}>
      <input {...getInputProps()} />
      <p>Drag 'n' drop some files here, or click to select files</p>
    </div>
  )
}

function yearBiggerThanCurrent(year) {
    return parseInt(year) > currentYear;
}

class AddEntry extends Component {
    constructor(props){
        super(props)

        this.state = {
            title:'',
            year: '',
            author: '',
            images: [],
            errorMessage: '',
        }


        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangeYear = this.onChangeYear.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.onFileUpload = this.onFileUpload.bind(this);
        this.onFilesUpload = this.onFilesUpload.bind(this);

    }

    onChangeTitle = (e) => {
        this.setState({
            title: e.target.value,
        })
    }

    onChangeAuthor = (e) => {
        this.setState({
            author: e.target.value,
        })
    }

    onChangeYear = (e) => {
        console.log(yearBiggerThanCurrent(e.target.value))
        let errorMessage = (e.target.value.match(/^[1-9]\d{0,3}$|^0$/g) 
                            && !yearBiggerThanCurrent(e.target.value) ) 
                            ? ""
                            : "El año tiene que ser 0 o mayor, con un formato válido (01 no es válido), y menor que el año actual"

        this.setState({
            year: e.target.value,
            errorMessage: errorMessage,
        })
    }

    onFileUpload = (e) => {
        console.log(e.target.files)
    }

    onFilesUpload = (files) => {
        console.log(files)
    }

    onSubmit() {
        if (!this.state.title){
            this.setState({
                errorMessage: "Introduce título por favor"
            })
            return null;
        }

        if (!this.state.author){
            this.setState({
                errorMessage: "Introduce autor por favor"
            })
            return null;
        }

        if (!this.state.year){
            this.setState({
                errorMessage: "Introduce año por favor"
            })
            return null;
        }

        if (yearBiggerThanCurrent(this.state.year) ){
            this.setState({
                errorMessage: "El año tiene que ser menor o igual al actual"
            })
            return null;
        }

        if (this.state.images.length === 0){
            this.setState({
                errorMessage: "Introduce por lo menos una imagen"
            })
            return null;
        }
    }

    render() {
        return(
            <div className="adduser_wrapper">
                <div className="adduser_info_wp">
                    <h3> Añadir un registro </h3>
                </div>

                <div className="form_add_entry height_100">
                    <form>
                        <label>Título</label>
                        <br/>
                        <input  className="addEntry_title"
                                value={this.state.title}
                                onChange={this.onChangeTitle}
                                type="text"
                                required
                                />
                        <br/>

                        <label>Autor</label>
                        <br/>
                        <input value={this.state.author}
                                onChange={this.onChangeAuthor}
                                type="text"
                                required
                                />

                        <br/>
                        <label>Año de creación</label>
                        <br/>
                        <input value={this.state.year}
                                onChange={this.onChangeYear}
                                type="text"
                                pattern="^[1-9]\d{1,4}$"
                                required
                                />
                        <br/>

                        <label> Imágenes </label>
                        <br/>
                        <input
                            type="file"
                            accept="image/png, image/jpeg"
                            multiple={true}
                            onChange={this.onFileUpload}
                        />


                        <Dropzone 
                        className="dropzone"
                        onDropCallBack={this.onFilesUpload}
                        />

                    </form>
                </div>

                <button 
                    type="submit"
                    className="btn_create_user"
                    onClick={this.onSubmit}
                    > 
                    Confirmar crear registro </button>
                <div className="login_error">
                    {this.state.errorMessage}                    
                </div>

            </div>
        )
    }
}

export default AddEntry;
