import React, { Component } from 'react';

import axios from 'axios';

import Header from './generics/header'
import Table from './generics/table'


import 'rc-slider/assets/index.css';
import 'rc-tooltip/assets/bootstrap.css';

import Slider from 'rc-slider';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const Range = createSliderWithTooltip(Slider.Range);


const AuthorList = ({onChange, authors}) => (
    <select name=""
            id="author_profile"
            onChange={onChange}>
        <option value="">Autores</option>
        {authors.map(author => <option key={author[0]} value={author[0]}>{author[0]}</option>)}
    </select>
);

const OrderBySelector = ({onChange, name}) => (
    <select name={name}
        onChange={onChange}
        >
        <option value="0" value="">Ordenar...</option>
        <option key="1" value="ASCENDING">Ascendiente</option>
        <option key="-1" value="DESCENDING">Descendiente</option>
    </select>
);

const EntryDetail = ({entry, base_url}) => (
    <div className="entryDetail" key={entry.id}>
        <div className="entryMeta">
            <div className="em_title">TÃ­tulo: {entry.title}</div>
            <div className="em_author">Author: {entry.author}</div>
            <div className="em_year">AÃ±o: {entry.year}</div>
        </div>
        <div className="entryImages">
            {
                entry.entry_images.map(img => 
                    <div key={entry.id + "-" + img.id} className="entryImageDetail">
                        <img className="eid_image" id={img.id} src={base_url + img.image}/>
                    </div>     
                )
            }
        </div>
    </div>
)

const EntriesList = ({entries, base_url}) => (

    <div className="table_container">
        {
            entries.length>0 ?
            (
                entries.map(entry =>
                    <EntryDetail entry={entry} base_url={base_url}/>
                )
            ) : (
                <div key="no_results" className="no_results"> No se han encontrado resultados </div>
            )
        }
    </div>
);


class Search extends Component {

    constructor(props){
        super(props)

        console.log("SEARCH PROPS", props)

        this.state = {
            data: props.location.state.detail,
            query_text: '',

            filter_author: '',
            filter_from_year: '',
            filter_to_year: '',

            order_by_year: '',
            order_by_author: '',
            order_by_title: '',

            page: '',
            page_size: '',

            isLoggedIn: true,

            searchResults: this.props.location.state.detail.data,
            authors: [],
            min_year: '',
            max_year: '',

            previous_query_text: this.props.query_text,
            page_size: 5,
            base_url: this.props.location.state.base_url
        }

        this.headers = [
                {label: "Titulo", id: "title", numeric: false, disablePadding: true},
                {label: "Autor", id: "author", numeric: false, disablePadding: true},
                {label: "AÃ±o", id: "year", numeric: true, disablePadding: true},
                {label: "Imagenes", id: "entry_images", numeric: false, disablePadding: true},
            ];

        this.handle = this.handle.bind()
        this.onAuthorSelect = this.onAuthorSelect.bind(this)
        this.onAuthorOrder = this.onAuthorOrder.bind(this)
        this.onTitleOrder = this.onTitleOrder.bind(this)
        this.onYearOrder = this.onYearOrder.bind(this)

        this.fetchData = this.fetchData.bind(this)
    }

    fetchData(){
        axios.get(this.state.base_url + '/rest_entry/', 
        {
            params: {
                page_size: this.state.page_size,
                q: this.state.query_text,
                order_year: this.state.order_by_year,
                order_author: this.state.order_by_author,
                order_title: this.state.order_by_title,
                from_year: this.state.filter_from_year,
                to_year: this.state.filter_to_year,
                author: this.state.filter_author
            }
        })
        .then(
            res => {
                this.setState({data: res.data})
            }
        )
    }

    componentDidMount(){
        console.log(this.state)
        axios.get(this.state.base_url + '/entry_meta').then(
            res => {
                this.setState({
                    authors:  res.data.authors,
                    min_year: parseInt(res.data.min_year),
                    max_year: parseInt(res.data.max_year),
                    filter_from_year: parseInt(res.data.min_year),
                    filter_to_year: parseInt(res.data.max_year),
                })
            }
        )
    }

    handle = (props) => {
        const { value, dragging, index, ...restProps } = props;
        this.setState({
            filter_from_year: props[0],
            filter_to_year: props[1],
        })            
    };


    handleInputChange = (e) => {
        this.setState({query_text: e.target.value});
    }

    onAuthorSelect = (e) => {
        this.setState({filter_author: e.target.value});
    } 

    onAuthorOrder = (e) => {
        this.setState({order_by_author: e.target.value})
    }

    onTitleOrder = (e) => {
        this.setState({order_by_title: e.target.value})
    }

    onYearOrder = (e) => {
        this.setState({order_by_year: e.target.value})
    }

    render() {

    if (!this.state.min_year || !this.state.max_year) {
        return <div />
    }

    return (
        <div className="search_wrapper">
            <div className="search_header">
                <Header isLoggedIn={this.state.isLoggedIn}/>
            </div>
            <div className="search_body">
                <div className="search_filters col-xs-4 col-md-2">
                    <div className="search_filter xs-12">
                        <div className="sf_name">
                            Filtro por autor
                        </div>
                        <div className="sf_filter">
                            <AuthorList
                                onChange={this.onAuthorSelect}
                                authors={this.state.authors}
                            />
                        </div>
                    </div>
                    <div className="search_filter xs-12">
                        <div className="sf_name">
                        Ordenar por autor
                        </div>
                        <div className="sf_filter">
                            <OrderBySelector
                                onChange={this.onAuthorOrder}
                                name="order_by_author"
                            />
                        </div>
                    </div>

                    <div className="search_filter xs-12">
                        <div className="sf_name">
                            Ordenar por titulo
                        </div>
                        <div className="sf_filter">
                            <OrderBySelector
                                onChange={this.onTitleOrder}
                                name="order_by_title"
                            />
                        </div>
                    </div>
                    <div className="search_filter xs-12">                        
                        <div className="sf_name">
                            Ordenar por aÃ±o
                        </div>
                        <div className="sf_filter">
                            <OrderBySelector
                                onChange={this.onYearOrder}
                                name="order_by_year"
                            />
                        </div>
                    </div>
                    <div className="search_filter xs-12">
                        <div className="sf_name">
                            Filtro por aÃ±o
                        </div>
                        <div className="sf_filter">
                            <Range 
                                min={this.state.min_year} 
                                max={this.state.max_year} 
                                defaultValue={[this.state.min_year, this.state.max_year]} 
                                onChange={this.handle}
                            />
                        </div>
                        <div className="sf_value">
                            {this.state.filter_from_year} - {this.state.filter_to_year}
                        </div>
                    </div>
                    <div className="search_filter xs-12">
                        <button onClick={this.fetchData} className="sf_name">
                            Refinar la busqueda!
                        </button>
                    </div>
                </div>
                <div className="search_results_wrapper col-xs-8 col-md-10">
                    <div className="search_bar xs-12">

                    </div>
                    <div className="search_results">
                        <div className="sr_searchbar">
                            <input 
                                onChange={this.handleInputChange} 
                                type="text" 
                                id="searchBarSearch" 
                                className="form-control col-xs-9 display_inline_block" 
                                placeholder={this.state.previous_query_text} 
                                required="" 
                                autoFocus={true}/>
                            <span className="input-group-btn col-xs-2">
                                <button onClick={this.fetchData} className="btn btn-search" type="button">
                                    <i className="fa fa-search fa-fw display_inline_block"></i>
                                </button>
                            </span>
                        </div>

                        <EntriesList entries={this.state.data.entries} base_url={this.state.base_url}/>


                    </div>
                </div>
            </div>
            <div className="search_footer xs-12">
            </div>

        </div>
    );

    }
}

export default Search;
