import React, { Component } from 'react';

import Entries from './entries';
import Users from './users';

import Header from './generics/header'

class Profile extends Component {
    constructor(props){
        super(props)

        this.state = {
            body_class: document.body.className,
            username: props.location.state.username,
            base_url: props.base_url,

            admin_entries_selected: true,
            list_entries_selected: true,

            admin_users_selected: false,
            list_users_selected: true,

        }
    }

    componentWillMount(){

        document.body.className = this.state.body_class + 
            ( this.state.body_class ? ' ' : '') + 'profile-body';

        // get info: name and photo, change state
    }

    componentWillUnmount(){
        document.body.className = this.state.body_class;
    }

    render() {
        return (
            <div className="profile-container">

                <Header username={this.state.username}/>

                <div className="navbar-options row">
                    <div className="col-sm-6 navbar-option selected">Administrar registros</div>
                    <div className="col-sm-6 navbar-option">Administrar usuarios</div>
                </div>
                <div className="data-container" 
                    style={{display: !this.state.admin_entries_selected}}>
                    <Entries show={this.state.list_entries_selected} 
                            base_url={this.state.base_url} />
                    { /* <AddEntry show={!this.state.list_entries_selected}  /> */ }
                </div>
                {console.log("STATE", this.state)}
                <div style={{display: !this.state.admin_entries_selected}}>
                    <Users show={this.state.list_entries_selected} />
                    { /* <AddUser show={this.state.list_entries_selected} /> */ }
                </div>
            </div>
        )
    }
}

export default Profile;
