import React, { Component } from 'react';

import axios from 'axios';

import Header from './generics/header'
import Table from './generics/table'


import 'rc-slider/assets/index.css';
import 'rc-tooltip/assets/bootstrap.css';

import Slider from 'rc-slider';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const Range = createSliderWithTooltip(Slider.Range);


const AuthorList = ({onChange, authors, value}) => (
    <select name=""
            id="author_profile"
            onChange={onChange}
            value={value}>
        <option value="">Lista de autores</option>
        {authors.map(author => <option key={author[0]} value={author[0]}>{author[0]}</option>)}
    </select>
);


class Search extends Component {

    constructor(props){
        super(props)

        this.state = {
            data: props.location.state.detail,
            query_text: '',

            filter_author: '',
            filter_from_year: '',
            filter_to_year: '',

            order_by_year: '',
            order_by_author: '',
            order_by_title: '',

            page: '',
            page_size: '',

            isLoggedIn: true,

            searchResults: this.props.location.state.detail.data,
            authors: [],
            min_year: '',
            max_year: '',
        }

        this.headers = [
                {label: "Titulo", id: "title", numeric: false, disablePadding: true},
                {label: "Autor", id: "author", numeric: false, disablePadding: true},
                {label: "AÃ±o", id: "year", numeric: true, disablePadding: true},
                {label: "Imagenes", id: "entry_images", numeric: false, disablePadding: true},
            ];

        this.handle = this.handle.bind()
        this.onAuthorSelect = this.onAuthorSelect.bind()
    }

    componentDidMount(){
        axios.get('http://127.0.0.1:8000/entry_meta').then(
            res => {
                this.setState({
                    authors:  res.data.authors,
                    min_year: parseInt(res.data.min_year),
                    max_year: parseInt(res.data.max_year),
                    filter_from_year: parseInt(res.data.min_year),
                    filter_to_year: parseInt(res.data.max_year),
                })
            }
        )

        axios.get('http://127.0.0.1:8000/rest_entry', {
            parms: {
                page_size: 50,
                q: this.state.query_text,
            }
        })
        .then(
            res => {
                this.setState({data: res.data})
            }
        )
    }

    handle = (props) => {
        const { value, dragging, index, ...restProps } = props;
        this.setState({
            filter_from_year: props[0],
            filter_to_year: props[1],
        })            
        console.log(this.state)
    };

    onAuthorSelect = (e) => {
        this.setState({filter_author: e.target.value});
        console.log(this.state)
    } 

  render() {

    if (!this.state.min_year || !this.state.max_year) {
        return <div />
    }

    return (
        <div className="search_wrapper">
            <div className="search_header">
                <Header isLoggedIn={this.state.isLoggedIn}/>
            </div>
            <div className="search_body">
                <div className="search_filters col-xs-4">
                    <div className="search_filter xs-12">
                        <div className="sf_name">
                            Filtro por autor
                        </div>
                        <div className="sf_filter">
                            <AuthorList
                                onChange={this.onAuthorSelect}
                                authors={this.state.authors}
                            />
                        </div>
                    </div>
                    <div className="search_filter xs-12">Order by author</div>
                    <div className="search_filter xs-12">Order by title</div>
                    <div className="search_filter xs-12">Order by year</div>
                    <div className="search_filter xs-12">
                        <div className="sf_name">
                            Filtro por aÃ±o
                        </div>
                        <div className="sf_filter">
                            <Range 
                                min={this.state.min_year} 
                                max={this.state.max_year} 
                                defaultValue={[this.state.min_year, this.state.max_year]} 
                                onChange={this.handle}
                            />
                        </div>
                        <div className="sf_value">
                            {this.state.filter_from_year} - {this.state.filter_to_year}
                        </div>
                    </div>
                    <div className="search_filter xs-12">
                        <button className="sf_name">
                            Refinar la buscqueda!
                        </button>
                    </div>
                </div>
                <div className="search_results_wrapper col-xs-8">
                    <div className="search_bar xs-12">

                    </div>
                    <div className="search_results">
                        <Table data={this.state.data}/>
                    </div>
                </div>
            </div>
            <div className="search_footer xs-12">
            </div>

        </div>
    );

    }
}

export default Search;
