import React, { Component } from 'react'
import Dropzone from 'react-dropzone'

function testClick(e) {
    e.preventDefault();
    console.log("Click!")
}

function arrayUnique(array) {
    var a = array.concat();
    for(var i=0; i<a.length; ++i) {
        for(var j=i+1; j<a.length; ++j) {
            if(a[i].name === a[j].name)
                a.splice(j--, 1);
        }
    }

    return a;
}

const currentYear = new Date().getYear() + 1900;

function yearBiggerThanCurrent(year) {
    return parseInt(year) > currentYear;
}

class AddEntry extends Component {
    constructor(props){
        super(props)

        this.state = {
            title:'',
            year: '',
            author: '',
            images: [],
            errorMessage: '',
        }


        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangeYear = this.onChangeYear.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.onFilesUpload = this.onFilesUpload.bind(this);

    }

    onChangeTitle = (e) => {
        this.setState({
            title: e.target.value,
        })
    }

    onChangeAuthor = (e) => {
        this.setState({
            author: e.target.value,
        })
    }

    onChangeYear = (e) => {
        let errorMessage = (e.target.value.match(/^[1-9]\d{0,3}$|^0$/g) 
                            && !yearBiggerThanCurrent(e.target.value) ) 
                            ? ""
                            : "El año tiene que ser 0 o mayor, con un formato válido (01 no es válido), y menor que el año actual"

        this.setState({
            year: e.target.value,
            errorMessage: errorMessage,
        })
    }

    onFilesUpload = (files) => {
        this.setState({
            images: arrayUnique(this.state.images.concat(files)),
        })
    }

    onSubmit() {
        if (!this.state.title){
            this.setState({
                errorMessage: "Introduce título por favor"
            })
            return null;
        }

        if (!this.state.author){
            this.setState({
                errorMessage: "Introduce autor por favor"
            })
            return null;
        }

        if (!this.state.year){
            this.setState({
                errorMessage: "Introduce año por favor"
            })
            return null;
        }

        if (yearBiggerThanCurrent(this.state.year) ){
            this.setState({
                errorMessage: "El año tiene que ser menor o igual al actual"
            })
            return null;
        }

        if (this.state.images.length === 0){
            this.setState({
                errorMessage: "Introduce por lo menos una imagen"
            })
            return null;
        }
    }

    render() {
        return(

            <div className="adduser_wrapper">

                <div className="adduser_info_wp">
                    <h3> Añadir un registro </h3>
                </div>

                <div className="form_add_entry height_100">
                    <form>
                        <label>Título</label>
                        <br/>
                        <input  className="addEntry_title"
                                value={this.state.title}
                                onChange={this.onChangeTitle}
                                type="text"
                                required
                                />
                        <br/>

                        <label>Autor</label>
                        <br/>
                        <input value={this.state.author}
                                onChange={this.onChangeAuthor}
                                type="text"
                                required
                                />

                        <br/>
                        <label>Año de creación</label>
                        <br/>
                        <input value={this.state.year}
                                onChange={this.onChangeYear}
                                type="text"
                                pattern="^[1-9]\d{1,4}$"
                                required
                                />
                        <br/>

                        <br/> <br/>
                        <label> Imágenes </label>
                        <br/>

                        <Dropzone
                        onDrop={acceptedFiles => this.onFilesUpload(acceptedFiles)}
                        oinClick={testClick}
                        >
                        {({getRootProps, getInputProps, isDragActive}) => (
                            <section>
                            <div style={{fontSize: "small"}}> Los nombres de las imagenes tienen que ser únicos </div>
                            <div {...getRootProps()}
                                className="dropzone"
                            >
                                <input {...getInputProps()} />
                                {
                                isDragActive 
                                    ? <p className="drop_label">Suelta las imágenes aqui... </p> 
                                    : <p className="drop_label"> Arrastra y suelta las imágenes, o had click aqui para seleccionar imagenes</p>
                                }

                                {
                                    this.state.images.map(
                                        image => (
                                            <div className="addEntry_thumbnail"
                                                key={image.name}
                                                >
                                                <img
                                                    className="addEntry_thumbnail_img" 
                                                    src={URL.createObjectURL(image)} 
                                                    alt={image.name}
                                                    onClick={(e) => testClick(e)}
                                                    />

                                            </div>
                                            )
                                    )
                                }
                            </div>
                            </section>
                        )}
                        </Dropzone>

                    </form>
                </div>

                <button 
                    type="submit"
                    className="btn_create_user"
                    onClick={this.onSubmit}
                    > 
                    Crear registro </button>
                <div className="login_error">
                    {this.state.errorMessage}                    
                </div>

            </div>
        )
    }
}

export default AddEntry;
