import React, { Component } from 'react';

class Login extends Component {
    constructor(props){
        super(props)

        this.state = {
            body_class: document.body.className,
        }
    }

    componentWillMount(){
        this.setState({
            body_class: document.body.className
        })
        document.body.className = this.state.body_class + 
            ( this.state.body_class ? ' ' : '') + 'text-center display_flex';
        document.body.style = 'background-color:#f5f5f5';
    }

    componentWillUnmount(){
        document.body.className = this.state.body_class;
        document.body.style = null;
    }

    render(){
        return (
            <div>
                <div class="float-left left-arrow-go-back"><i class="fas fa-arrow-left"></i></div>
                <div className="form_wrapper login_page height_100 margin_auto">


                    <form id="form-signin ">
                        <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
                        <label htmlFor="inputEmail" className="sr-only">Email address</label>
                        <input type="email" id="inputEmail" className="form-control margin-b-10" placeholder="Email address" required="" autoFocus={true}/>
                        <label htmlFor="inputPassword" className="sr-only">Password</label>
                        <input type="password" id="inputPassword" className="form-control margin-b-10" placeholder="Password" required=""/>
                        <button className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
                        <p className="mt-5 mb-3 text-muted">You need to have authorized credentials to login, you cannot sign up yourself</p>
                    </form>
                </div>
            </div>
        )
    }
}

export default Login;
