import React, { Component } from 'react'
import Dropzone from 'react-dropzone'

import axios from 'axios';

const currentYear = new Date().getYear() + 1900;
const base_url = 'http://127.0.0.1:8000'

function arrayUnique(array) {
    var a = array.concat();
    for(var i=0; i<a.length; ++i) {
        for(var j=i+1; j<a.length; ++j) {
            if(a[i].name === a[j].name)
                a.splice(j--, 1);
        }
    }

    return a;
}

function yearBiggerThanCurrent(year) {
    return parseInt(year) > currentYear;
}

class AddEntry extends Component {
    constructor(props){
        super(props)

        this.state = {
            title:'',
            year: '',
            author: '',
            images: [],
            errorMessage: '',

            new_entry_id: '',
            modal_display: '',
        }

        this.onChangeTitle = this.onChangeTitle.bind(this);
        this.onChangeAuthor = this.onChangeAuthor.bind(this);
        this.onChangeYear = this.onChangeYear.bind(this);

        this.onSubmit = this.onSubmit.bind(this);
        this.reviewImages = this.reviewImages.bind(this);

        this.onFilesUpload = this.onFilesUpload.bind(this);
        this.onImgRemove = this.onImgRemove.bind(this);

        this.closeModal = this.closeModal.bind(this);
    }

    onChangeTitle = (e) => {
        this.setState({
            title: e.target.value,
        })
    }

    onChangeAuthor = (e) => {
        this.setState({
            author: e.target.value,
        })
    }

    onChangeYear = (e) => {
        let errorMessage = (e.target.value.match(/^[1-9]\d{0,3}$|^0$/g) 
                            && !yearBiggerThanCurrent(e.target.value) ) 
                            ? ""
                            : "El año tiene que ser 0 o mayor, con un formato válido (01 no es válido), y menor que el año actual"

        this.setState({
            year: e.target.value,
            errorMessage: errorMessage,
        })
    }

    onFilesUpload = (files) => {
        this.setState({
            images: arrayUnique(this.state.images.concat(files)),
        })
    }

    onImgRemove = (e, name) => {
        e.preventDefault();
        e.stopPropagation();
        var imgs = this.state.images;
        this.setState({
            images: imgs.filter(img => img.name !== name),
        })
    }

    onSubmit(e) {
        e.preventDefault();
        e.stopPropagation();

        if (!this.state.title){
            this.setState({
                errorMessage: "Introduce título por favor"
            })
            return null;
        }

        if (!this.state.author){
            this.setState({
                errorMessage: "Introduce autor por favor"
            })
            return null;
        }

        if (!this.state.year){
            this.setState({
                errorMessage: "Introduce año por favor"
            })
            return null;
        }

        if (yearBiggerThanCurrent(this.state.year) ){
            this.setState({
                errorMessage: "El año tiene que ser menor o igual al actual"
            })
            return null;
        }

        if (this.state.images.length === 0){
            this.setState({
                errorMessage: "Introduce por lo menos una imagen"
            })
            return null;
        }

        let headers = {
            "Content-Type":"multipart/form-data",
            'Authorization': `Token ${localStorage.getItem("token")}`,
        }

        const formData = new FormData();
        formData.append("title", this.state.title)
        formData.append("author", this.state.author)
        formData.append("year", this.state.year)


        for (const image of this.state.images) {
            formData.append('images', image)
        }

        axios.post(base_url + '/entry/',
            formData,
            {
                headers: {
                    "Content-Type":"multipart/form-data",
                    'Authorization': `Token ${localStorage.getItem("token")}`,
                }
            }
        ).then(
            res => {
                this.setState({
                    title:'',
                    year: '',
                    author: '',
                    images: [],
                    errorMessage: '',
                    new_entry_id: res.entry_id,
                })

                this.reviewImages(res.entry_id);
            }
        )
    }

    closeModal() {
        this.setState({
            modal_display: 'none',
            new_entry_id: '',
        })
    }

    reviewImages(){

    }

    render() {

        return(

            <div className="adduser_wrapper">

                <div className="modal_entry"  style={{display: this.state.modal_display}} >
                    <div className="modal_entry_detail">
                        <button type="button" className="close" id="btn_close_modal" aria-label="Close" onClick={this.closeModal}>
                            <span aria-hidden="true">&times;</span>
                        </button>
                        <div className="row">
                            Registro creado correctamente, ¿quieres revisar ahora las imagenes?
                            Si eliges no hacerlo, lo puedes hacer más tarde editando el registro
                        </div>
                        <div>
                            <button type="button" 
                                    id="edit_user_edit"
                                    className="btn btn-primary" 
                                    onClick={this.reviewImages}>
                                    Editar 
                                    </button>

                            <button onClick={this.closeModal} 
                                    type="button" 
                                    className="btn btn-secondary" 
                                    data-dismiss="modal">
                                    Cancelar
                                    </button>
                        </div>

                    </div>
                </div>

                <div className="reviewWrapper">
                    <div className="ReactModal__Overlay ReactModal__Overlay--after-open" 
                        style="position: fixed; inset: 0px; background-color: transparent; z-index: 1000;">

                        <div style="position: absolute; inset: 0px; border: medium none; background: transparent none repeat scroll 0% 0%; overflow: hidden; border-radius: 0px; outline: currentcolor none medium; padding: 0px;" 
                             className="ReactModal__Content ReactModal__Content--after-open" 
                             tabindex="-1" 
                             role="dialog" 
                             aria-label="Lightbox">

                            <div className="ril-outer ril__outer ril__outerAnimating  " style="transition: opacity 300ms ease 0s; animation-duration: 300ms; animation-direction: reverse;" tabindex="-1">

                                <div class="ril-inner ril__inner">
                                    <div> Test? </div>
                                </div>

                                <button type="button" className="ril-prev-button ril__navButtons ril__navButtonPrev" aria-label="Previous image"></button>
                                <button type="button" className="ril-next-button ril__navButtons ril__navButtonNext" aria-label="Next image"></button>

                                <div className="ril-toolbar ril__toolbar">
                                    <ul className="ril-toolbar-left ril__toolbarSide ril__toolbarLeftSide">
                                        <li className="ril-toolbar__item ril__toolbarItem">
                                        <span className="ril-toolbar__item__child ril__toolbarItemChild"></span>
                                        </li>
                                    </ul>

                                    <ul className="ril-toolbar-right ril__toolbarSide ril__toolbarRightSide">
                                        <li className="ril-toolbar__item ril__toolbarItem">
                                            <button type="button" aria-label="Zoom in" className="ril-zoom-in ril__toolbarItemChild ril__builtinButton ril__zoomInButton"></button>
                                        </li>
                                        <li className="ril-toolbar__item ril__toolbarItem">
                                            <button type="button" aria-label="Zoom out" className="ril-zoom-out ril__toolbarItemChild ril__builtinButton ril__zoomOutButton ril__builtinButtonDisabled" disabled=""></button>
                                        </li>
                                        <li className="ril-toolbar__item ril__toolbarItem">
                                            <button type="button" aria-label="Close lightbox" className="ril-close ril-toolbar__item__child ril__toolbarItemChild ril__builtinButton ril__closeButton"></button>
                                        </li>
                                    </ul>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>

                <div className="adduser_info_wp">
                    <h3> Añadir un registro </h3>
                </div>

                <div className="form_add_entry height_100">
                    <form>
                        <label>Título</label>
                        <br/>
                        <input  className="addEntry_title"
                                value={this.state.title}
                                onChange={this.onChangeTitle}
                                type="text"
                                required
                                />
                        <br/>

                        <label>Autor</label>
                        <br/>
                        <input value={this.state.author}
                                onChange={this.onChangeAuthor}
                                type="text"
                                required
                                />

                        <br/>
                        <label>Año de creación</label>
                        <br/>
                        <input value={this.state.year}
                                onChange={this.onChangeYear}
                                type="text"
                                pattern="^[1-9]\d{0,3}$|^0$"
                                required
                                />
                        <br/>

                        <br/> <br/>
                        <label> Imágenes </label>
                        <br/>

                        <Dropzone
                        onDrop={acceptedFiles => this.onFilesUpload(acceptedFiles)}>
                        {({getRootProps, getInputProps, isDragActive}) => (
                            <section>
                            <div style={{fontSize: "small", fontStyle:"italic"}}> Los nombres de las imagenes tienen que ser únicos </div>
                            <div {...getRootProps()}
                                className="dropzone">
                                <input {...getInputProps()} />
                                {
                                    this.state.images.length === 0
                                    ? isDragActive 
                                        ? <p className="drop_label row">Suelta las imágenes aqui... </p> 
                                        : <p className="drop_label row">Arrastra y suelta las imágenes, o had click aquí para seleccionar imagenes</p> 
                                    : null
                                }

                                {
                                    this.state.images.map(
                                        image => (
                                            <div className="addentry_row row"
                                                key={image.name}>
                                                <div className="col-10">{image.name}</div>
                                                <div className="col-2" onClick={(e) => this.onImgRemove(e, image.name)}> Borrar </div>
                                            </div>
                                            )
                                    )
                                }
                            </div>
                            </section>
                        )}
                        </Dropzone>

                        <button 
                            type="submit"
                            className="btn_create_user"
                            onClick={this.onSubmit}
                            > 
                            Crear registro </button>
                        <div className="login_error">
                            {this.state.errorMessage}                    
                        </div>

                    </form>
                </div>



            </div>
        )
    }
}

export default AddEntry;
