import React, { Component } from 'react';

function Greeting(props) {
    console.log("REF", window.location.href);
    if( props.userName !== ""){
        return <a className="nav-link active login-landing" href="/profile">Bienvenido, {props.userName}</a>;
    } else {
        return <a className="nav-link active login-landing" href="/login">Login</a>;
    }
}

class Header extends Component {
    constructor(props){
        super(props)

        this.state = {
            body_class: document.body.className,
            userName: props.username || '',
            userPhoto: '',
            isLoggedIn: props.isLoggedIn,
        }

        this.logout = this.logout.bind(this);
    }

    logout(e) {
        e.preventDefault();
        localStorage.removeItem("token");
        this.props.history.push({
            pathname: '/landing',
        })
    }

    render(){
        return (
            <div className="row profile-header">
                <div id="uc3m_logo">
                <Dropdown>
                <Dropdown.Toggle variant="success" id="dropdown-basic">
                    Dropdown Button
                </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                </Dropdown.Menu>
                </Dropdown>;
                    <image src={'../static/img/portada_logo.png'} alt="UC3M Logo"/>
                </div>
                <div id="profile-avatar">

                    <Greeting isLoggedIn={this.state.isLoggedIn} userName={this.state.userName}/>
                </div>
            </div>
            )
        }    
}

export default Header;
