        //         // localStorage.setItem("token", res.data.token);
        //         // this.props.history.push({
        //         //     pathname: '/profile',
        //         //     state: {
        //         //             base_url: this.state.base_url,
        //         //         }
        //         // })
        //     }
        // );
        // .catch(){

        //     }
    }

    render(){
        return (
            <div className="position-relative">
                <div className="float-left left-arrow-go-back" onClick={this.props.history.goBack}>
                    <i className="fas fa-2x fa-arrow-left"></i>
                </div>
                <div className="form_wrapper login_page height_100 margin_auto">
                    <form id="form-signin ">
                        <h1 className="h3 mb-3 font-weight-normal">Please sign in</h1>
                        <label htmlFor="inputEmail" className="sr-only">Email address</label>
                        <input onChange={this.onChangeEmail} type="email" id="inputEmail" className="form-control margin-b-10" placeholder="Email address" required="" autoFocus={true}/>
                        <label htmlFor="inputPassword" className="sr-only">Password</label>
                        <input onChange={this.onChangePassword} type="password" id="inputPassword" className="form-control margin-b-10" placeholder="Password" required=""/>
                        <button className="btn btn-lg btn-primary btn-block" 
                                type="submit" 
                                onClick={this.login}>Sign in</button>
                        <p className="mt-5 mb-3 text-muted">Necesitas tener credenciales autorizadas para hacer login, no puedes registrarte por tu cuenta</p>
                    </form>
                </div>
            </div>
        )
    }
}

export default Login;
