import 'rc-slider/assets/index.css';
import 'rc-tooltip/assets/bootstrap.css';

import Slider from 'rc-slider';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const Range = createSliderWithTooltip(Slider.Range);

const AuthorList = ({onChange, authors, value}) => (
    <select name=""
            id="author_profile"
            onChange={onChange}
            value={value}
            >
        <option value="">Autores</option>
        {authors.map(author => <option key={author[0]} value={author[0]}>{author[0]}</option>)}
    </select>
);

const OrderBySelector = ({onChange, name, value}) => (
    <select name={name}
        onChange={onChange}
        value={value}
        >
        <option value="0">Ordenar...</option>
        <option key="1" value="ASCENDING" >Ascendiente</option>
        <option key="-1" value="DESCENDING" >Descendiente</option>
    </select>
);

const EntryDetail = ({entry, base_url, onClick}) => (
    <div className="entryDetail ed_profile" key={entry.id} data-id={entry.id} onClick={onClick}>
        <div className="entryEdit">
            <div className="e_edit"><i className="fas fa-edit"></i></div>
            <div className="e_delete"><i className="fas fa-trash-alt"></i></div>
        </div>

        <div className="entryMeta">
            <div className="em_title">Título: {entry.title}</div>
            <div className="em_author">Author: {entry.author}</div>
            <div className="em_year">Año: {entry.year}</div>
        </div>
        <div className="entryImages ei_profile">
            {
                entry.entry_images.map(img => 
                    <div key={entry.id + "-" + img.id} className="entryImageDetail">
                        <img alt="" className="eid_image" id={img.id} src={base_url + img.image}/>
                    </div>     
                )
            }
        </div>
    </div>
)

const EntriesList = ({entries, base_url, onClick}) => (

    <div className="table_container">
        {
            entries.length>0 ?
            (
                entries.map(entry =>
                    <EntryDetail key={entry.id} entry={entry} base_url={base_url} onClick={onClick}/>
                )
            ) : (
                <div key="no_results" className="no_results"> No se han encontrado resultados </div>
            )
        }
    </div>
);

const Button = ({name, disabled, onClick}) => (

    <button 
        className={ disabled ? name + " disabled_pagination" : name } 
        disabled={disabled} 
        onClick={onClick}
    />
);

// Due to setState delay issues, variable managed on a higher level
let page = 0;

class Entries extends Component {

    constructor(props){
        super(props)

        console.log(props);

        this.state = {
            data: {},
            query_text: '',

            filter_author: '',
            filter_from_year: '',
            filter_to_year: '',

            order_by_year: '',
            order_by_author: '',
            order_by_title: '',
            order_by_created: '',

            isLoggedIn: true,

            author:   '',
            min_year: '',
            max_year: '',

            page_size: 5,

            base_url: props.base_url,
            total_elements: 0,

            waiting_to_change: false,

            modal_display: 'none',
            modal_title: '',
            modal_year: '',
            modal_author: '',
            modal_images: [],

            display_entry_search: '',
        }

        this.headers = [
                {label: "Titulo", id: "title", numeric: false, disablePadding: true},
                {label: "Autor", id: "author", numeric: false, disablePadding: true},
                {label: "Año", id: "year", numeric: true, disablePadding: true},
                {label: "Imagenes", id: "entry_images", numeric: false, disablePadding: true},
            ];

        this.handle = this.handle.bind()
        this.onAuthorSelect = this.onAuthorSelect.bind(this)
        this.onAuthorOrder = this.onAuthorOrder.bind(this)
        this.onTitleOrder = this.onTitleOrder.bind(this)
        this.onYearOrder = this.onYearOrder.bind(this)

        this.fetchData = this.fetchData.bind(this)
        this.onPreviousPage = this.onPreviousPage.bind(this)
        this.onNextPage = this.onNextPage.bind(this)
        this.onEntryClick = this.onEntryClick.bind(this)
        this.closeModal = this.closeModal.bind(this)
        this.handleInputChange = this.handleInputChange.bind(this)
    }

    fetchData(){
        this.setState({total_elements:'', waiting_to_change:true})

        axios.get(this.state.base_url + '/entry/', 
        {
            params: {
                page_size: this.state.page_size,
                page: page,
                q: this.state.query_text,
                order_year: this.state.order_by_year,
                order_author: this.state.order_by_author,
                order_title: this.state.order_by_title,
                from_year: this.state.filter_from_year,
                to_year: this.state.filter_to_year,
                author: this.state.filter_author
            }
        })
        .then(
            res => {
                this.setState({
                    data: res.data,
                    total_elements: res.data.total,
                    waiting_to_change: false,
                })
            }
        )
    }

    componentWillMount(){
        axios.get(this.state.base_url + '/entry_meta').then(
            res => {
                this.setState({
                    authors:  res.data.authors,
                    min_year: parseInt(res.data.min_year),
                    max_year: parseInt(res.data.max_year),
                    filter_from_year: parseInt(res.data.min_year),
                    filter_to_year: parseInt(res.data.max_year),
                })
            }
        )

        this.fetchData();
    }

    handle = (props) => {
        this.setState({
            filter_from_year: props[0],
            filter_to_year: props[1],
        })            
    };

    handleInputChange = (e) => {
        this.setState({query_text: e.target.value});
    }

    onAuthorSelect = (e) => {
        this.setState({filter_author: e.target.value});
    } 

    onAuthorOrder = (e) => {
        this.setState({order_by_author: e.target.value})
    }

    onTitleOrder = (e) => {
        this.setState({order_by_title: e.target.value})
    }

    onYearOrder = (e) => {
        this.setState({order_by_year: e.target.value})
    }

    onCreatedOrder = (e) => {
        this.setState({order_by_created: e.target.value})
    }

    onNextPage(){
        page += 1;
        this.fetchData()
    }

    onPreviousPage(){
        page -= 1;
        this.fetchData()
    }

    onEntryClick = (e) => {
        e.stopPropagation();
        e.preventDefault();

        let entry = this.state.data.entries.find(
                    entry => entry.id === parseInt(e.currentTarget.getAttribute("data-id")));

        this.setState({
            modal_title: entry.title,
            modal_year: entry.year,
            modal_author: entry.author,
            modal_images: entry.entry_images,
            modal_display: '',
        })
    }

    closeModal() {
        this.setState({
            modal_display: 'none',
            modal_title: '',
            modal_year: '',
            modal_author: '',
            modal_images: [],
        })
    }

    render() {

        console.log("this state", this.state);

        // TODO: modificar behaviour so that solo la tabla se quede sin renderizar hasta 
        // encontrar datos de nuevo
        if ( !this.state.min_year || !this.state.max_year || this.state.total_elements === '' ) {
            return <div />
        }

        if( this.state.waiting_to_change ) {
            return <div />
        }

        return (

        <div className="search_container">
            <div className="col-xs-12 search_header">
                <div className="admin_entries col-xs-6">
                    Listado de registros (permite editar y borrat)
                </div>
                <div className="add_entry col-xs-6">
                    Añadir registro
                </div>
            </div>


            <div className="search_wrapper" style={{display:this.state.display_entry_search}}>
                <div className="search_body">
                    <div className="search_filters col-xs-4 col-md-2">
                        <div className="sf_category xs-12">
                            Filtros
                        </div>
                        <div className="search_filter sf_border_bottom xs-12">
                            <div className="sf_name">
                                Autor
                            </div>
                            <div className="sf_filter">
                                <AuthorList
                                    onChange={this.onAuthorSelect}
                                    authors={this.state.authors}
                                    value={this.state.filter_author}
                                />
                            </div>
                        </div>
                        <div className="search_filter xs-12">
                            <div className="sf_name">
                                Filtro por año
                            </div>
                            <div className="sf_filter">
                                <Range 
                                    min={this.state.min_year} 
                                    max={this.state.max_year} 
                                    defaultValue={[this.state.filter_from_year, this.state.filter_to_year]} 
                                    onChange={this.handle}
                                />
                            </div>
                            <div className="sf_value">
                                {this.state.filter_from_year} - {this.state.filter_to_year}
                            </div>
                        </div>
                        <div className="sf_category xs-12">
                            Ordenar por...
                        </div>
                        <div className="search_filter sf_border_bottom xs-12">
                            <div className="sf_name">
                                Autor
                            </div>
                            <div className="sf_filter">
                                <OrderBySelector
                                    onChange={this.onAuthorOrder}
                                    name="order_by_author"
                                    value={this.state.order_by_author}
                                />
                            </div>
                        </div>

                        <div className="search_filter sf_border_bottom xs-12">
                            <div className="sf_name">
                                Fecha creación registro
                            </div>
                            <div className="sf_filter">
                                <OrderBySelector
                                    onChange={this.onCreatedOrder}
                                    name="order_by_reated"
                                    value={this.state.order_by_created}
                                />
                            </div>
                        </div>

                        <div className="search_filter sf_border_bottom xs-12">
                            <div className="sf_name">
                                Título
                            </div>
                            <div className="sf_filter">
                                <OrderBySelector
                                    onChange={this.onTitleOrder}
                                    name="order_by_title"
                                    value={this.state.order_by_title}
                                />
                            </div>
                        </div>
                        <div className="search_filter xs-12">                        
                            <div className="sf_name">
                                Año
                            </div>
                            <div className="sf_filter">
                                <OrderBySelector
                                    onChange={this.onYearOrder}
                                    name="order_by_year"
                                    value={this.state.order_by_year}
                                />
                            </div>
                        </div>
                        <div className="search_filter xs-12">
                            <button onClick={this.fetchData} className="sf_name">
                                Refinar la busqueda!
                            </button>
                        </div>
                    </div>
                    <div className="search_results_wrapper col-xs-8 col-md-10">
                        <div className="search_results">
                            <div className="sr_searchbar">
                                <input 
                                    onChange={this.handleInputChange} 
                                    type="text" 
                                    id="searchBarSearch" 
                                    className="form-control col-xs-9 display_inline_block" 
                                    value={this.state.query_text} 
                                    required="" 
                                    autoFocus={true}/>
                                <span className="input-group-btn col-xs-2">
                                    <button onClick={this.fetchData} className="btn btn-search" type="button">
                                        <i className="fa fa-search fa-fw display_inline_block"></i>
                                    </button>
                                </span>
                            </div>
                            <EntriesList entries={this.state.data.entries} base_url={this.state.base_url} onClick={this.onEntryClick}/>
                            { this.state.total_elements>0 ? 
                                <div className="pagination_container">
                                    <div className="total_elements"> 
                                        {(page*this.state.page_size)+1} - {Math.min(this.state.total_elements, ( (1+page) * this.state.page_size ) )} de {this.state.total_elements}
                                    </div>
                                    <Button name="previous_page_btn"
                                            disabled={page+1 === 1} 
                                            onClick={this.onPreviousPage}/>
                                    <Button name="next_page_btn" 
                                            disabled={page+1 === Math.ceil(this.state.total_elements/this.state.page_size)} 
                                            onClick={this.onNextPage}/>
                                </div>
                                :
                                <div></div>
                            }
                        </div>
                    </div>
                </div>

                <div className="search_footer xs-12">
                </div>

            </div>
        </div>

        )
    }

}

export default Entries;
