import React, { Component } from 'react';

import { Redirect } from 'react-router-dom';

import axios from 'axios';

import EditUserModal from '../user/editUserModal'

const base_url = "http://127.0.0.1:8000"

function Greeting(props) {
    var url = window.location.href.split('/')
    var current_location = url[url.length-1]
    if( props.userName !== ""){
        return <div className="user_profile_wp">
                    <div className="up_greeting">
                        Bienvenido, {props.userName}    
                    </div>
                    <div className="up_dropdown">
                        { current_location !== 'profile'
                            ? <div className="row">
                                    Ir a sección administrador
                                </div>
                            : null
                        }
                        <div className="row"
                            onClick={props.onClickEdit}
                        >
                            Editar tus datos
                        </div>
                        <div className="row"
                            onClick={props.onClickLogout}
                        >
                            Cerrar sesión
                        </div>
                    </div>
              </div>

        // return <a className="nav-link active login-landing" href="/profile">Bienvenido, {props.userName}</a>;
    } else {
        return <a className="nav-link active login-landing" href="/login">Login</a>;
    }
}

class Header extends Component {
    constructor(props){
        super(props)

        this.state = {
            redirectToLanding: false,
            user: {},
            display_edit_modal: 'none'
        }

        this.logout = this.logout.bind(this);
        this.onClickEdit = this.onClickEdit.bind(this);
        this.closeModal = this.closeModal.bind(this);

        this.fetchUserMeta = this.fetchUserMeta.bind(this);


    }

    componentWillMount(){
        this.fetchUserMeta()
    }

    onClickEdit() {
        console.log("Edit");
        this.setState({
            display_edit_modal: '',
        })
    }

    closeModal() {
        this.setState({
            display_edit_modal: 'none',
        })
    }

    logout() {
        localStorage.removeItem("token");
        this.setState({
            redirectToLanding: true,
        })
    }

    fetchUserMeta() {
        axios.get(base_url + '/user_meta/', 
        {
            headers: {
                'Authorization': `Token ${localStorage.getItem("token")}`,
            }
        }).then(
            res => {
                this.setState({
                    user: res.data.user,
                })
                localStorage.setItem("user", res.data.user.id)
            }
        ).catch(
            error => {
                this.logout();
            }
        )
    }

    render(){
        if (this.state.redirectToLanding) {
            return <Redirect to='/login' />
        }

        return (

            <div className="row profile-header">
                    <div style={{display:this.state.display_edit_modal}}>
                        <EditUserModal user={this.user}/>
                    </div>

                <div id="uc3m_logo">

                    <image src={'../static/img/portada_logo.png'} alt="UC3M Logo"/>
                </div>
                <div id="profile-avatar">

                    <Greeting 
                        userName={this.state.user.username}
                        onClickLogout={this.logout}
                        onClickEdit={this.onClickEdit}
                        />
                </div>
            </div>
            )
        }    
}

export default Header;
