import React, { Component } from 'react';

function Greeting(props) {
    // const isLoggedIn = props.isLoggedIn;
    console.log(localStorage, localStorage.getItem("logged"))
    if( localStorage.getItem("logged") === 'true'){
        return <a className="nav-link active login-landing" href="/profile">Bienvenido, {props.userName}</a>;
    } else {
        return <a className="nav-link active login-landing" href="/login">Login</a>;
    }
}

class Header extends Component {
    constructor(props){
        super(props)

        this.state = {
            body_class: document.body.className,
            userName: props.username || "Guest",
            userPhoto: "",
            isLoggedIn: props.isLoggedIn,
        }
    }

    render(){
        return (
            <div className="row profile-header">
                <div id="uc3m_logo">
                    <image src={'../static/img/portada_logo.png'} alt="UC3M Logo"/>
                </div>
                <div id="profile-avatar">
                    <div id="pa-name" className="col-sm-7">
                        <Greeting isLoggedIn={this.state.isLoggedIn} userName={this.state.userName}/>
                    </div>
                    <div id="pa-photo" className="col-sm-4">
                        <image src={this.state.userPhoto} alt="Avatar photo"/>
                    </div>
                </div>
            </div>
            )
        }    
}

export default Header;
