import React, { Component } from 'react';

import axios from 'axios';

class AddUser extends Component {
    constructor(props){
        super(props)

        this.state = {
            new_username: '',
            new_useremail: '',
            new_userpassword: '',
        }

        this.onChangeNewUser = this.onChangeNewUser.bind(this);
        this.onChangeNewEmail = this.onChangeNewEmail.bind(this);
        this.onChangeNewPassword = this.onChangeNewPassword.bind(this);
    }

    onChangeNewUser = (e) => {
        this.setState({
            new_username: e.target.value,
        })
    }

    onChangeNewEmail = (e) => {
        this.setState({
            new_useremail: e.target.value,
        })
    }

    onChangeNewPassword = (e) => {
        this.setState({
            new_userpassword: e.target.value,
        })
    }

    render() {
        return (
            <div className="adduser_wrapper">
                <div className="row"> <h3> Añadir un usuario </h3> </div>
                <div className="row"> Atención, este usuario tendrá todos los 
                    permisos para crear, consultar, editar y borrar registros y otros usuarios
                    </div>

                <div className="adduser_form">
                    <form>
                        <label className="username_label">Usuario</label>
                        <input value={this.state.new_username}
                                onChange={this.onChangeNewUser}
                            ></input>
                        <label className="useremail_label">Usuario</label>
                        <input value={this.state.new_useremail}
                                onChange={this.onChangeNewEmail}
                            ></input>

                        <label className="userpass_label">Usuario</label>
                            <input value={this.state.new_userpassword}
                                onChange={this.onChangeNewPassword}
                            ></input>
                    </form>

                </div>
            </div>
        )
    }
}

export default AddUser;


