import React, { Component } from 'react';

import axios from 'axios';

import Header from './generics/header'

import 'rc-slider/assets/index.css';
import 'rc-tooltip/assets/bootstrap.css';

import Slider from 'rc-slider';

import { AuthorList, OrderBySelector } from './search';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const Range = createSliderWithTooltip(Slider.Range);


const EntryDetail = ({entry, base_url, onClick, onRemoveClick}) => (
    <div className="entryDetail ed_profile" key={entry.id} data-id={entry.id} onClick={onClick}>
        <div className="entryEdit">
            <div className="e_edit"><i className="fas fa-edit"></i></div>
            <div className="e_delete" onClick={() => onRemoveClick(entry.id)}><i className="fas fa-trash-alt"></i></div>
        </div>

        <div className="entryMeta">
            <div className="em_title">Título: {entry.title}</div>
            <div className="em_author">Author: {entry.author}</div>
            <div className="em_year">Año: {entry.year}</div>
        </div>
        <div className="entryImages ei_profile">
            {
                entry.entry_images.map(img => 
                    <div key={entry.id + "-" + img.id} className="entryImageDetail">
                        <img alt="" className="eid_image" id={img.id} src={base_url + img.image}/>
                    </div>     
                )
            }
        </div>
    </div>
)

const EntriesList = ({entries, base_url, onClick, onRemoveClick}) => (

    <div className="table_container">
        {
            entries.length>0 ?
            (
                entries.map(entry =>
                    <EntryDetail key={entry.id} entry={entry} base_url={base_url} onClick={onClick} onRemoveClick={onRemoveClick}/>
                )
            ) : (
                <div key="no_results" className="no_results"> No se han encontrado resultados </div>
            )
        }
    </div>
);

const Button = ({name, disabled, onClick}) => (

    <button 
        className={ disabled ? name + " disabled_pagination" : name } 
        disabled={disabled} 
        onClick={onClick}
    />
);

const DeleteModal = ({title, onClick, selectedId, onCancelClick}) => (
    <div className="delete_modal">
        <div className="dm_message">¿Estás seguro de querer borrar el registro {title}?</div>
        <div className="dm_btn_wrapper">
        <button type="button" 
                className="btn btn-primary" 
                id="dm_btn_delete"
                onClick={() => onClick(selectedId)}>
                Borrar 
                </button>

        <button onClick={onCancelClick} 
                type="button" 
                className="btn btn-secondary" 
                data-dismiss="modal">
                Cancelar
                </button>
        </div>
    </div>
);

// Due to setState delay issues, variable managed on a higher level
let page = 0;

class Entries extends Component {

    constructor(props){
        super(props)

        this.state = {
            base_url: props.base_url,
        }
    }


    render() {

        return (

        <div className="search_container">
            <div className="navbar-options row search_header">
                <div className="admin_entries navbar-option selected col-6">
                    Listado de registros (permite editar y borrar)
                </div>
                <div className="add_entry navbar-option col-6">
                    Añadir registro
                </div>
            </div>


            <div className="search_wrapper" style={{display:this.state.display_entry_search}}>

            </div>

        </div>

        )
    }

}

export default Entries;
