import React, { Component } from 'react';

class Landing extends Component {
    constructor(props){
        super(props)

        this.state = {
            body_class: document.body.className,
        }
    }

    componentWillMount(){
        //fetch("http://127.0.0.1:8000/image/1/")
        fetch("http://127.0.0.1:8000/health/")
            .then(res => { res.json() } )
            .then(
               res =>  console.log(res) 
            )

        document.body.className = this.state.body_class + ( this.state.body_class ? ' ' : '') + 'text-center height_100 landing';
    }

    componentWillUnmount(){
        document.body.className = this.state.body_class;
    }

    /*
        text-center height_100 landing
    */

    render() {
        return (
            <div className="d-flex w-100 h-100 p-3 mx-auto flex-column wrapper_main">
            <header className="masthead mb-auto">
                <div className="inner">
                <h3 className="float-left">Nobe</h3>
                <nav className="nav justify-content-center float-right">
                    <a className="nav-link active login-landing" href="/login">Login</a>
                </nav>
                </div>
            </header>

            <main role="main" className="inner" id="inner_wrapper">
                <h1>Start searching.</h1>
                <p className="lead">Nobe te permite buscar texto en imágenes manuscritas.</p>
                <div className="col-xs-12 display_block">
                    <input type="text" id="searchBar" className="form-control main-search-bar col-xs-10 display_inline_block" placeholder="Start searching here..." required="" autoFocus={true}/>
                    <span className="input-group-btn col-xs-2 main-span-button">
                        <button className="btn btn-search main-search-button" type="button"><i className="fa fa-search fa-fw display_inline_block"></i></button>
                    </span>
                </div>

            </main>

            <footer className="mastfoot mt-auto">
                <div className="inner">
                <p>Trabajo de Fin de Grado por <a href="https://github.com/ForFer">Fernando</a>, para la © Universidad Carlos III de Madrid,l 2019</p>
                </div>
            </footer>
            </div>

        )
    }
}

export default Landing;

